import { useState, useEffect } from "react"

function useStats() {
    const [stats, setStats] = useState;
    useEffect(() => {
        async function fetchData() {
            console.log('fetching data');
            const data = await fetch('api.coingecko.com/api/v3/coins/list').then(data => data.json());
        }
        fetchData();
        
    }, [])
}

function Stats() {
    const stats = useStats();
    
    return <div>
        <p>Stats:</p>
    </div>
}

export default function IndexPage() {
    return (
        <div>
            <Stats></Stats>
        </div>
    )
}
