import { useState, useEffect } from "react"

function useStats() {
    const [stats, setStats] = useState();
    useEffect(() => {
        async function fetchData() {
            console.log('fetching data');
            const data = await fetch('httpapi.coingecko.com/api/v3/coins/list').then(data => data.json());
        }
        fetchData();
        
    }, [])
    return stats;
}

function Stats() {
    const stats = useStats();
    console.log(stats)
    return <div>
        <p>Stats:</p>
    </div>
}

export default function IndexPage() {
    return (
        <div>
            <Stats></Stats>
        </div>
    )
}
