import { useState, useEffect } from "react"
import CoinInfo from "./styles.js"
var numeral = require('numeral')

function useStats() {
    const [stats, setStats] = useState();
    useEffect(() => {
        async function fetchData() {
            console.log('fetching data');
            const data = await fetch('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd').then(res => res.json()
            );
            setStats(data);
        }
        fetchData();
        
    }, [])
    return stats;
}

function Stats() {
    const stats = useStats();
    if(!stats) return <p>Loading...</p>;
    console.log(stats)

    return (
    <div>
        <div className='statBlock'>
                {stats.map(coin => {
                    return (
                        <CoinInfo>
                            <img stylesrc={coin.image}></img>
                            <h3>{coin.name}</h3>
                            <h3>{coin.symbol}</h3>
                            <h3>{coin.current_price}</h3>
                            <h3>{numeral(coin.circulating_supply).format()}</h3>
                            <h3>{numeral(coin.market_cap).format('$0,0[.]00')}</h3>
                        </CoinInfo>
                    )
                })}
        </div>
    </div>
    )
}

export default function IndexPage() {
    return (
        <div>
            <p>Powered by <a href='https://www.coingecko.com/en/api#explore-api'>CoinGecko API</a></p>
            <Stats></Stats>
        </div>
    )
}
